function submitForm(r){$.ajax({type:"POST",url:"main.php",dataType:"json",data:r,success:function(r){1==r.success?(hideErrors(),$("#result").val(r.response)):(showErrors(r.response),$("#result").val(""))}})}function showErrors(r){$("#error").show(),$("#error").html(r)}function hideErrors(){$("#error").hide(),$("#error").html("")}$(document).ready(function(){hideErrors(),$("#result").val(""),$("#convert").click(function(r){r.preventDefault(),submitForm($("#converter").serialize())})});